$(function(){const t=$(".loader"),e=$(".articles");t.hide(),$(".selection").on("change",".selectors",function(){t.show();const i=$(this).val();""!==i&&$.ajax({method:"get",url:"https://api.nytimes.com/svc/topstories/v2/"+i+".json?api-key=D5H6I9oboArY2CG6wjPNy9BAvjnLZOey"}).done(function(i){e.empty(),t.hide();const n=i.results.filter(t=>t.multimedia[4]).slice(0,12);$.each(n,function(t,i){const n='<li><div class="art-content"><a href='+i.url+" target=“_blank”><img src="+i.multimedia[4].url+'><div class="text-content" ><p>'+i.abstract+"</p></div></a></div></li>";e.append(n)})}).always(function(){})})});