$(function(){const t=$(".loading"),n=$(".articles");t.show(),$(".selection").on("change",".selectors",function(){const e=$(this).val();""!==e&&$.ajax({method:"get",url:"https://api.nytimes.com/svc/topstories/v2/"+e+".json?api-key=D5H6I9oboArY2CG6wjPNy9BAvjnLZOey"}).done(function(t){n.empty();const e=t.results.filter(t=>t.multimedia).slice(0,12);$.each(e,function(t,e){const o="<img src="+e.multimedia[4].url+"><p>"+e.abstract+"</p>";n.append(o)})}).always(function(){t.hide()})})});