$(function(){const t=$(".loading"),e=$(".articles");t.hide(),$(".selection").on("change",".selectors",function(){const i=$(this).val();$.ajax({method:"get",url:"https://api.nytimes.com/svc/topstories/v2/"+i+".json?api-key=D5H6I9oboArY2CG6wjPNy9BAvjnLZOey"}).done(function(i){e.empty(),t.hide();const n=i.results.filter(t=>t.multimedia).slice(0,12);$.each(n,function(t,i){const n=i.title,o="<img src="+i.multimedia[4].url+"><h1>"+n+"</h1><p>"+i.abstract+"</p>";e.append(o)})}).always(function(){t.show()})})});