$(function(){const t=$(".loading"),n=$(".articles");t.show(1500),$(".selection").on("change",".selectors",function(){const e=$(this).val();""!==e&&$.ajax({method:"get",url:"https://api.nytimes.com/svc/topstories/v2/"+e+".json?api-key=D5H6I9oboArY2CG6wjPNy9BAvjnLZOey"}).done(function(t){n.empty();const e=t.results.filter(t=>t.multimedia).slice(0,12);$.each(e,function(t,e){const i='<li><div class="art-content"><a href='+e.url+" target=“_blank”><img src="+e.multimedia[4].url+'><div class="text-content" ><p>'+e.abstract+"</p></div></a></div></li>";n.append(i)})}).always(function(){t.hide(50)})})});