$(function(){const t=$(".loading"),e=$(".articles");t.show(),$(".selection").on("change",".selectors",function(){const n=$(this).val();""!==n&&$.ajax({method:"get",url:"https://api.nytimes.com/svc/topstories/v2/"+n+".json?api-key=D5H6I9oboArY2CG6wjPNy9BAvjnLZOey"}).done(function(t){e.empty();const n=t.results.filter(t=>t.multimedia).slice(0,12);$.each(n,function(t,n){const i="<li><a href="+n.url+" target=“_blank”><img src="+n.multimedia[4].url+"><p>"+n.abstract+"</p></a></li>";e.append(i)})}).always(function(){t.hide()})})});